// Parse Claude response and extract JSON
const response = $input.first().json;
const content = response.content[0].text;

// Extract JSON from response
const jsonMatch = content.match(/```json\n([\s\S]*?)\n```/);
let article;

if (jsonMatch) {
  article = JSON.parse(jsonMatch[1]);
} else {
  article = JSON.parse(content);
}

// Ensure description is max 80 chars
article.description = article.description?.slice(0, 80) || article.excerpt?.slice(0, 80);

// Calculate reading time
const wordCount = article.content.split(/\s+/).length;
article.readingTime = Math.max(1, Math.ceil(wordCount / 200));

return [{ json: article }];

