// Build prompts for article generation
const topics = [
  { topic: 'Enterprise LLM Integration Best Practices', pillar: 'ai-automation' },
  { topic: 'Real World Asset Tokenization Trends', pillar: 'digital-assets' },
  { topic: 'Measuring AI Transformation ROI', pillar: 'consulting' },
  { topic: 'Latest AI Model Announcements Analysis', pillar: 'industry-news' }
];

// Select topic based on day of week
const dayIndex = new Date().getDay();
const selected = topics[dayIndex % topics.length];

const systemPrompt = `You are an expert content writer for AUTOBLOG. Generate a high-quality, SEO-optimized article.

Return JSON with: title (50-60 chars), slug, description (max 80 chars), excerpt (150-200 chars), content (markdown), tags (array), seo { metaTitle, metaDescription }`;

const userPrompt = `Generate an article about: ${selected.topic}\nContent pillar: ${selected.pillar}\nTarget length: 1200-1500 words`;

return [{ json: { systemPrompt, userPrompt, ...selected } }];
